(()=>{const e=document.getElementById("passwords-div");document.addEventListener("DOMContentLoaded",()=>{(async()=>{try{e.innerHTML="<p>Loading passwords...</p>";const d=await chrome.runtime.sendMessage({action:"listAllUserData"});if(!d||0===d.length)return void(e.innerHTML="<p>Just fetch through nillion daashboard for all ids</p>");e.innerHTML="<h2>Saved Passwords:</h2>",d.forEach(d=>{const n=document.createElement("div");n.innerText=d.websiteName||d.name,n.style.padding="5px",n.style.borderBottom="1px solid #ddd",e.appendChild(n)})}catch(d){console.error("Failed to load passwords:",d),e.innerHTML="<p>Failed to load passwords</p>"}})()})})();